"use strict";

window.addEventListener("DOMContentLoaded", function () {
  var headerItems = document.querySelector('.header__items'),
      headerItem = document.querySelectorAll('.header__item'),
      hamburger = document.querySelector('.hamburger');
  hamburger.addEventListener('click', function () {
    if (!hamburger.classList.contains('hamburger_active')) {
      hamburger.setAttribute('aria-expanded', true);
    } else {
      hamburger.setAttribute('aria-expanded', false);
    }

    hamburger.classList.toggle('hamburger_active');
    headerItems.classList.toggle('header__items_active');
  });
  headerItem.forEach(function (item) {
    item.addEventListener('click', function () {
      hamburger.setAttribute('aria-expanded', false);
      hamburger.classList.toggle('hamburger_active');
      headerItems.classList.toggle('header__items_active');
    });
  });
  $(document).ready(function () {
    $('.slider').slick({
      autoplay: true,
      autoplaySpeed: 6000,
      appendDots: ".slider__controller",
      dots: true,
      dotsClass: 'slider__eclipse'
    });
  });
  var scrollBtn = document.querySelector('.header__scrolldown');
  scrollBtn.addEventListener('click', function (e) {
    window.scrollBy(0, 774);
  });
  var allCards = document.querySelectorAll('.team-card');
  var openCardsBtn = document.querySelector('.team .btn--empty');
  openCardsBtn.addEventListener('click', function (e) {
    allCards.forEach(function (item) {
      if (item.hasAttribute('hidden')) {
        item.classList.add('animate__animated', 'animate__fadeIn');
      }

      item.removeAttribute('hidden');
    });
    openCardsBtn.classList.add('animate__animated', 'animate__fadeOut');
    openCardsBtn.setAttribute('hidden', "true");
  });
  console.log(allCards);
});